import React, { useState } from 'react';
import zxcvbn from 'zxcvbn';

const questions = [
  'What was your first pet’s name?',
  'What is your mother’s maiden name?',
  'What was the name of the first street you lived on?',
  'What is your favorite color?',
];

const pictures = [
  'picture1.jpg',
  'picture2.jpg',
  'picture3.jpg',
  'picture4.jpg',
  'picture5.jpg',
  'picture6.jpg',
  'picture7.jpg',
  'picture8.jpg',
  'picture9.jpg',
];

const App = () => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [passwordStrength, setPasswordStrength] = useState('');
  const [passwordQuestions, setPasswordQuestions] = useState([]);
  const [picturePassword, setPicturePassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission
  };

  const handlePasswordChange = (event) => {
    const passwordValue = event.target.value;
    setPassword(passwordValue);

    const strength = zxcvbn(passwordValue).score;
    setPasswordStrength(strength);
  };

  const handleQuestionsChange = (event) => {
    const selectedQuestions = Array.from(event.target.selectedOptions, (option) => option.value);
    setPasswordQuestions(selectedQuestions);
  };

  const handlePicturePasswordChange = (event) => {
    setPicturePassword(event.target.value);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Full Name:
        <input type="text" value={fullName} onChange={(event) => setFullName(event.target.value)} />
      </label>
      <br />
      <label>
        Email:
        <input type="email" value={email} onChange={(event) => setEmail(event.target.value)} />
      </label>
      <br />
      <label>
        Phone Number:
        <input type="tel" value={phoneNumber} onChange={(event) => setPhoneNumber(event.target.value)} />
      </label>
      <br />
      <label>
        Date of Birth:
        <input type="date" value={dateOfBirth} onChange={(event) => setDateOfBirth(event.target.value)} />
      </label>
      <br />
      <label>
        Username:
        <input type="text" value={username} onChange={(event) => setUsername(event.target.value)} />
      </label>
      <br />
      <label>
        Password:
        <input type="password" value={password} onChange={handlePasswordChange} />
      </label>
      {password && (
        <div>
          Password strength: {passwordStrength}
        </div>
      )}
      <br />
      <label>
        Choose 3 security questions:
        <select multiple value={passwordQuestions} onChange={handleQuestionsChange}>
          {questions.map((question) => (
            <option key={question} value={question}>
              {question}
            </option>
          ))}
        </select>
      </label>
      <br />
      <label>
  Choose a picture password:
  <select value={picturePassword} onChange={handlePicturePasswordChange}>
    <option value="">Select a picture</option>
    {pictures.map((picture) => (
      <option key={picture} value={picture}>
        {picture}
      </option>
    ))}
  </select>
</label>

          </form>
          )

        }

        export default App;
