import React, { useState } from 'react';
import zxcvbn from 'zxcvbn';
import PasswordStrengthMeter from './PasswordStrengthMeter';
const questions = [
  'What was your first pet’s name?',
  'What is your mother’s maiden name?',
  'What was the name of the first street you lived on?',
  'What is your favorite color?',
];

const pictures = [
  'picture1.jpg',
  'picture2.jpg',
  'picture3.jpg',
  'picture4.jpg',
  'picture5.jpg',
  'picture6.jpg',
  'picture7.jpg',
  'picture8.jpg',
  'picture9.jpg',
];

const App = () => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [dob, setDob] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [passwordStrength, setPasswordStrength] = useState(null);
  const [securityQuestions, setSecurityQuestions] = useState([questions]);
  const [securityAnswers, setSecurityAnswers] = useState([]);
  const [selectedPicture, setSelectedPicture] = useState(null);

  const handlePasswordChange = (event) => {
    const password = event.target.value;
    setPassword(password);
    setPasswordStrength(checkPasswordStrength(password));
  };

  const checkPasswordStrength = (password) => {
    // Use a password strength checking library such as zxcvbn to check the password strength
    // and return a score or a message
  };

  const handleSecurityQuestionChange = (event, index) => {
    const questions = [...securityQuestions];
    questions[index] = event.target.value;
    setSecurityQuestions(questions);
  };

  const handleSecurityAnswerChange = (event, index) => {
    const answers = [...securityAnswers];
    answers[index] = event.target.value;
    setSecurityAnswers(answers);
  };

  const handlePictureClick = (event, picture) => {
    setSelectedPicture(picture);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Send the registration form data to the server
  };

  return (
    <div>
      <h1>Create a New Account</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Full Name:
          <input type="text" value={fullName} onChange={(e) => setFullName(e.target.value)} />
        </label>
        <label>
          Email:
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </label>
        <label>
          Phone number:
          <input type="tel" value={phone} onChange={(e) => setPhone(e.target.value)} />
        </label>
        <label>
          Date of birth:
          <input type="date" value={dob} onChange={(e) => setDob(e.target.value)} />
        </label>
        <label>
          Username:
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </label>
        <label>
          Password:
          <input type="password" value={password} onChange={handlePasswordChange} />
          {passwordStrength && <PasswordStrengthMeter score={passwordStrength.score} />}
        </label>
        {securityQuestions.map((question, index) => (
          <div key={index}>
            <label>
              Security Question {index + 1}:
              <input type="text" value={question} onChange={(e) => handleSecurityQuestionChange(e, index)} />
            </label>
            <label>
              Security Answer {index + 1}:
              <input type="text" value={securityAnswers[index]} onChange={(e) => handleSecurityAnswerChange(e, index)} />
            </label>
          </div>
        ))}
        <label>
  Choose a picture password:
  <select value={picturePassword} onChange={handlePicturePasswordChange}>
    <option value="">Select a picture</option>
    {pictures.map((picture) => (
      <option key={picture} value={picture}>
        {picture}
      </option>
    ))}
  </select>
</label>

          </form>
          </div>
          )

        }

        export default App;
