import zxcvbn from 'zxcvbn';

function PasswordStrengthMeter({ password }) {
  const passwordScore = zxcvbn(password).score;

  const createProgressClassName = (score) => {
    switch (score) {
      case 0:
        return 'bg-danger';
      case 1:
        return 'bg-warning';
      case 2:
        return 'bg-info';
      case 3:
        return 'bg-primary';
      case 4:
        return 'bg-success';
      default:
        return '';
    }
  };

  return (
    <div className="password-strength-meter">
      <div
        className={`progress ${createProgressClassName(passwordScore)}`}
        style={{ height: '10px' }}
      >
        <div
          className="progress-bar"
          role="progressbar"
          style={{ width: `${(passwordScore + 1) * 20}%` }}
        />
      </div>
      <small className="text-muted">
        Password strength: {zxcvbn(password).feedback.warning}
      </small>
    </div>
  );
}

export default PasswordStrengthMeter;
