import React, { useState } from 'react';
import  './LandingPage.css'
import PasswordStrengthMeter from './PasswordStrengthMeter';
import { UsersData } from './UsersData';
const LandingPage = () => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [picturePassword, setPicturePassword] = useState('');
  const [securityQuestions, setSecurityQuestions] = useState([]);
  const [securityAnswers, setSecurityAnswers] = useState(['', '', '']);

  const pictures = [    'picture1.jpg',    'picture2.jpg',    'picture3.jpg',    'picture4.jpg',    'picture5.jpg',    'picture6.jpg',    'picture7.jpg',    'picture8.jpg',    'picture9.jpg',  ];

  const handleSecurityAnswerChange = (event, index) => {
    const answers = [...securityAnswers];
    answers[index] = event.target.value;
    setSecurityAnswers(answers);
  };

  const handleSecurityQuestionChange = (event, index) => {
    const questions = [...securityQuestions];
    questions[index] = event.target.value;
    setSecurityQuestions(questions);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handlePicturePasswordChange = (event) => {
    setPicturePassword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    UsersData.push({
      fullName,
      email,
      phone,
      dateOfBirth,
      username,
      password,
      picturePassword,
      securityQuestions,
      securityAnswers,
    })
  };

  return (
    <div className='container' >

    <form onSubmit={handleSubmit}>
    <h1>Registration Form</h1>
      <label>
        Full Name:
        <input type="text" value={fullName} onChange={(e) => setFullName(e.target.value)} />
      </label>
      <label>
        Email:
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      </label>
      <label>
        Phone Number:
        <input type="tel" value={phone} onChange={(e) => setPhone(e.target.value)} />
      </label>
      <label>
        Date of Birth:
        <input type="date" value={dateOfBirth} onChange={(e) => setDateOfBirth(e.target.value)} />
      </label>
      <label>
        Username:
        <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
      </label>
      <label>
        Password:
        <input type="password" value={password} onChange={handlePasswordChange} />
      </label>
      <PasswordStrengthMeter password={password} />
      {securityQuestions.map((question, index) => (
        <label key={index}>
          Security Question {index + 1}:
          <input
            type="text"
            value={question}
            onChange={(e) => handleSecurityQuestionChange(e, index)}
          />
        </label>
      ))}
      <label>
        Choose a picture password:
        <select value={picturePassword} onChange={handlePicturePasswordChange}>
          <option value="">Select a picture</option>
          {pictures.map((picture) => (
            <option key={picture} value={picture}>
             {picture} </option>
))}
</select>
</label>
{securityAnswers.map((answer, index) => (
<label key={index}>
Security Answer {index + 1}:
<input
type="text"
value={answer}
onChange={(e) => handleSecurityAnswerChange(e, index)}
/>
</label>
))}
<button type="submit">Submit</button>
</form>
</div>
);
};

export default LandingPage;
